mapper.select:
select * from (select g.*, m.profile, row_number() over(order by ${orderBy} desc, seq desc) rnum from grouplist g inner join member m on g.writer_id = m.id
${ing} = #{ingValue}
and
DBMS_LOB.INSTR(contents, #{keywordValue}) > 0
${keywordType} like '%${keywordValue}%'
hobby_type like '%${hobby_type}%'
and period = #{period}
and to_date(start_date) > to_date(#{start_date}) and to_date(#{end_date}) > to_date(end_date)
and location like '%${location}%'
) where rnum between #{start} and #{end}

select count(*) from (select g.*, m.profile, row_number() over(order by ${orderBy} desc, seq desc) rnum from grouplist g inner join member m on g.writer_id = m.id
${ing} = #{ingValue}
and
DBMS_LOB.INSTR(contents, #{keywordValue}) > 0
${keywordType} like '%${keywordValue}%'
hobby_type like '%${hobby_type}%'
and period = #{period}
and to_date(start_date) > to_date(#{start_date}) and to_date(#{end_date}) > to_date(end_date)
and location like '%${location}%'
)

mapper.update:
update grouplist set title = #{title}, hobby_type = #{hobby_type},
	apply_start = #{apply_start}, apply_end = #{apply_end},
	start_date = #{start_date}, end_date = #{end_date}, period = #{period},
	max_num = #{max_num}, location = #{location}, location_lat = #{location_lat},
	location_lng = #{location_lng}, contents = #{contents}
	where seq = #{seq}

mapper.select:
select * from (select grouplist.*, row_number() over(order by seq desc) rnum from grouplist
hobby_type like '%${hobby_arr}%'
) where 6 > rnum

select * from group_app

	where parent_seq in

		#{seq}
